mapper.select:

select

from student_report
where student_id = #{student_id,jdbcType=INTEGER}
  and teacher_id = #{teacher_id,jdbcType=INTEGER}

mapper.delete:

delete from student_report
where student_id = #{student_id,jdbcType=INTEGER}
  and teacher_id = #{teacher_id,jdbcType=INTEGER}

mapper.insert:

insert into student_report (student_id, teacher_id, name,
  temperature, remarks, date
  )
values (#{student_id,jdbcType=INTEGER}, #{teacher_id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR},
  #{temperature,jdbcType=DOUBLE}, #{remarks,jdbcType=VARCHAR}, #{date,jdbcType=TIMESTAMP}
  )

mapper.update:

update student_report
set name = #{name,jdbcType=VARCHAR},
  temperature = #{temperature,jdbcType=DOUBLE},
  remarks = #{remarks,jdbcType=VARCHAR},
  date = #{date,jdbcType=TIMESTAMP}
where student_id = #{student_id,jdbcType=INTEGER}
  and teacher_id = #{teacher_id,jdbcType=INTEGER}
